    private void ButtonSegiitigaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButtonSegiitigaActionPerformed
        // TODO add your handling code here:
        int a = Integer.parseInt(AlasSegitiga.getText());
        int t = Integer.parseInt(TinggiSegitiga.getText());
        double hasil =(a * t)/2;
        HasilSegitiga.setText(String.valueOf(hasil));
    }//GEN-LAST:event_ButtonSegiitigaActionPerformed

