    private void ButtonBalokActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButtonBalokActionPerformed
        // TODO add your handling code here:
        int p = Integer.parseInt(PanjangBalok.getText());
        int l = Integer.parseInt(LebarBalok.getText());
        int t = Integer.parseInt(TinggiBalok.getText());
        double hasil = 2 * ((p*l)+(p*t)+(l*t));
        HasilBalok.setText(String.valueOf(hasil));
    }//GEN-LAST:event_ButtonBalokActionPerformed

